import { Component } from '@angular/core';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
  // Example data to display in the dashboard (optional, customize as needed)
  notes = [
    { type: 'Regular Note', content: 'This is a regular note.' },
    { type: 'Reminder', content: 'Doctor’s appointment at 4 PM.' },
    { type: 'ToDo', content: 'Complete the Angular project.', completed: false },
    { type: 'Bookmark', content: 'https://angular.io/' }
  ];

  constructor() {}

  markAsComplete(index: number) {
    if (this.notes[index].type === 'ToDo') {
      this.notes[index].completed = true;
    }
  }
}
